mapper.select:
SELECT
	b.*,
	(	SELECT count(*)
		FROM tb_recommend r
		WHERE r.recName = #{memnick }
			AND r.bodno = b.bodno
	) AS isExistsLikeData,
	(	SELECT count(*)
		FROM tb_recommend r
		WHERE r.bodno = b.bodno
	) AS recommendCnt,
	(	SELECT count(*)
		FROM tb_keep k
		WHERE k.kepnick = #{memnick }
			AND k.bodno = b.bodno
	) AS isExistsPinData,
	(	SELECT count(*)
		FROM tb_keep k
		WHERE k.bodno = b.bodno
	) AS pinCnt
FROM TB_BOARD b
WHERE
	BODNAME IN (
		SELECT f.FLWID
		FROM TB_FOLLOW f
		WHERE f.MEMID= #{memid}
	)
	OR bodname=#{memnick}
ORDER BY BODDATE DESC
