SELECT flwid recommend, worth FROM ( SELECT rownum rnum, F.* FROM ( SELECT flwid, COUNT(flwid) worth FROM (
SELECT flwid FROM tb_follow F, tb_member M WHERE 1=1 AND F.memid = M.memid AND M.memid IN ( SELECT flwid FROM tb_follow F, tb_member M WHERE 1=1 AND F.memid = M.memid AND M.memid = #{memid} ) AND flwid NOT IN ( SELECT flwid FROM tb_follow F, tb_member M WHERE 1=1 AND F.memid = M.memid AND M.memid = #{memid} )
) GROUP BY flwid ORDER BY worth desc ) F ) R WHERE rnum <= 10 ORDER BY worth desc


	SELECT memid FROM (
	    SELECT rownum rnum, F.* FROM(
	        SELECT memid FROM tb_follow
	        where flwid=#{_value}
	        ORDER BY memid
	        )F
	        ORDER BY rnum
	    )R
	    WHERE R.rnum <=10
